import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './Chat.css';

function Chat() {
    const { chatId } = useParams();
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState('');
    const user = JSON.parse(localStorage.getItem('user'));

    useEffect(() => {
        fetchMessages();
    }, []);

    const fetchMessages = () => {
        axios.get(`http://localhost:8081/messages/${chatId}`)
            .then(res => {
                console.log('Fetched messages:', res.data); // Debug log
                setMessages(res.data);
            })
            .catch(err => {
                console.log('Error fetching messages:', err);
            });
    };

    const sendMessage = () => {
        axios.post('http://localhost:8081/messages', {
            chat_id: chatId,
            user_email: user.email,
            message
        })
        .then(res => {
            setMessages([...messages, { user_email: user.email, message }]);
            setMessage('');
        })
        .catch(err => {
            console.log('Failed to send message', err);
        });
    };

    return (
        <div>
            <div>
                {messages.map((msg, index) => (
                    <div key={index}>{msg.user_email}: {msg.message}</div>
                ))}
            </div>
            <input
                type="text"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Type your message..."
            />
            <button onClick={sendMessage}>Send</button>
        </div>
    );
}

export default Chat;
