import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './Chat.css';

const Chat = () => {
    const { chatId } = useParams();
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const [chats, setChats] = useState([]);
    const userId = localStorage.getItem('userId');
    const navigate = useNavigate();

    useEffect(() => {
        if (!chatId) return;

        const fetchMessages = async () => {
            try {
                const response = await axios.get(`http://localhost:8081/messages/${chatId}`);
                setMessages(response.data);
            } catch (error) {
                console.error('Error fetching messages:', error);
            }
        };

        fetchMessages();
    }, [chatId]);

    useEffect(() => {
        const fetchInbox = async () => {
            try {
                const response = await axios.get(`http://localhost:8081/inbox/${userId}`);
                setChats(response.data);
            } catch (error) {
                console.error('Error fetching inbox:', error);
            }
        };

        fetchInbox();
    }, [userId]);

    const handleSendMessage = async () => {
        if (!newMessage.trim()) return;

        try {
            await axios.post(`http://localhost:8081/messages`, {
                chat_id: chatId,
                user_email: localStorage.getItem('userEmail'),
                message: newMessage
            });

            setNewMessage('');
            setMessages([...messages, { user_name: 'You', message: newMessage }]);
        } catch (error) {
            console.error('Error sending message:', error);
        }
    };

    const handleChatClick = (sellerId) => {
        navigate(`/chat/${sellerId}`);
    };

    return (
        <div className="chat-container">
            <div className="inbox-sidebar">
                {chats.map((chat) => (
                    <div key={chat.seller_id} className="chat-item" onClick={() => handleChatClick(chat.seller_id)}>
                        {chat.seller_name}
                    </div>
                ))}
            </div>
            <div className="chat-content">
                <h1>Chat</h1>
                <div className="messages">
                    {messages.map((message, index) => (
                        <div key={index} className={`message ${message.user_name === 'You' ? 'sent' : 'received'}`}>
                            <strong>{message.user_name}: </strong>{message.message}
                        </div>
                    ))}
                </div>
                <div className="new-message">
                    <input
                        type="text"
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                        placeholder="Type your message..."
                    />
                    <button onClick={handleSendMessage}>Send</button>
                </div>
            </div>
        </div>
    );
};

export default Chat;
