import React from 'react';
import './Post.css';

const Post = ({ post }) => {
    return (
        <div className="post-card">
            <img src={`http://localhost:8081/${post.picture}`} alt={post.title} />
            <h3>{post.title}</h3>
            <p>{post.description}</p>
            <div className="post-meta">
                <span>Posted by: {post.author}</span>
                <span>Price: â‚¬{post.price}</span>
                <span>Genre: {post.genre}</span>
                <span>Date: {new Date(post.time).toLocaleDateString()}</span>
            </div>
        </div>
    );
};

export default Post;
