import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Profile.css';

function Profile() {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      setUser(JSON.parse(userData));
    } else {
      navigate('/login'); // Redirect to login if not logged in
    }
  }, [navigate]);

  const handleProfilePicChange = (e) => {
    // Handle profile picture change logic here
  };

  const handleLogout = () => {
    // Handle logout logic here
  };

  return (
    <div className="profile-container">
      <div className="profile-info-container">
        <div className="profile-picture">
          <img src="path-to-profile-pic" alt="Profile" className="profile-pic-large" />
          <input type="file" onChange={handleProfilePicChange} />
          <button className="change-profile-pic-button">Change Profile Picture</button>
        </div>
        <div className="user-info">
          <h2>{user?.name}</h2>
          <p>{user?.email}</p>
          <div className="ratings">
            <span className="likes">üëç {user?.likes || 0}</span>
            <span className="dislikes">üëé {user?.dislikes || 0}</span>
          </div>
          <button className="log-out-button" onClick={handleLogout}>Log Out</button>
        </div>
      </div>
      <div className="posts-section">
        <div className="posts-container">
          <p className="no-posts">You haven't published a post yet.</p>
          <button className="create-post-button">Create Post</button>
        </div>
      </div>
    </div>
  );
}

export default Profile;
