import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FilterModal.css';

const FilterModal = ({ onClose, onFilterChange }) => {
  const [genres, setGenres] = useState([]);
  const [filters, setFilters] = useState({ sortBy: 'newest', genre: '' });

  useEffect(() => {
    fetchGenres();
  }, []);

  const fetchGenres = () => {
    axios.get('http://localhost:8081/genres')
      .then(res => {
        setGenres(res.data);
      })
      .catch(err => {
        console.log("Error fetching genres:", err);
      });
  };

  const handleSortChange = (e) => {
    setFilters({ ...filters, sortBy: e.target.value });
  };

  const handleGenreChange = (e) => {
    setFilters({ ...filters, genre: e.target.value });
  };

  const applyFilters = () => {
    onFilterChange(filters);
    onClose(); // Close the modal after applying filters
  };

  return (
    <div className="filter-modal">
      <div className="filter-modal-content">
        <button className="filter-modal-close" onClick={onClose}>X</button>
        <h2>Filter Posts</h2>
        <div className="filter-modal-group">
          <label htmlFor="sort">Sort By:</label>
          <select id="sort" value={filters.sortBy} onChange={handleSortChange}>
            <option value="newest">Newest</option>
            <option value="oldest">Oldest</option>
            <option value="priceAsc">Price: Low to High</option>
            <option value="priceDesc">Price: High to Low</option>
          </select>
        </div>
        <div className="filter-modal-group">
          <label htmlFor="genre">Genre:</label>
          <select id="genre" value={filters.genre} onChange={handleGenreChange}>
            <option value="">All</option>
            {genres.map(genre => (
              <option key={genre.id} value={genre.name}>{genre.name}</option>
            ))}
          </select>
        </div>
        <button className="apply-filters-button" onClick={applyFilters}>Apply Filters</button>
      </div>
    </div>
  );
};

export default FilterModal;
