import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import EditProfile from './EditProfile';
//import ChangePassword from './ChangePassword';  // Assume this component exists for demonstration
//import NotificationSettings from './NotificationSettings'; // Assume this component exists for demonstration
import Layout from './Layout';
import './Settings.css';

function Settings() {
    const [activeTab, setActiveTab] = useState('edit-profile');
    const navigate = useNavigate();

    const renderContent = () => {
        switch (activeTab) {
            case 'edit-profile':
                return <EditProfile />;
            case 'change-password':
                return <ChangePassword />;
            case 'notification-settings':
                return <NotificationSettings />;
            default:
                return <EditProfile />;
        }
    };

    return (
        <Layout>
            <div className="settings-page">
                <div className="settings-sidebar">
                    <h1>Settings</h1>
                    <ul className="settings-list">
                        <li
                            className={activeTab === 'edit-profile' ? 'active' : ''}
                            onClick={() => setActiveTab('edit-profile')}
                        >
                            Edit Profile
                        </li>
                        <li
                            className={activeTab === 'change-password' ? 'active' : ''}
                            onClick={() => setActiveTab('change-password')}
                        >
                            Change Password
                        </li>
                        <li
                            className={activeTab === 'notification-settings' ? 'active' : ''}
                            onClick={() => setActiveTab('notification-settings')}
                        >
                            Notification Settings
                        </li>
                    </ul>
                </div>
                <div className="settings-content">
                    {renderContent()}
                </div>
            </div>
        </Layout>
    );
}

export default Settings;
