import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import './CreatePost.css';

function EditPost() {
    const { id } = useParams();
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [author, setAuthor] = useState('');
    const [price, setPrice] = useState('');
    const [selectedImage, setSelectedImage] = useState(null);
    const [genre, setGenre] = useState('');
    const [genres, setGenres] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        fetchPost();
        fetchGenres();
    }, []);

    const fetchPost = async () => {
        try {
            const res = await axios.get(`http://localhost:8081/post/${id}`);
            const post = res.data;
            setTitle(post.title);
            setDescription(post.description);
            setAuthor(post.author);
            setPrice(post.price);
            setGenre(post.genre);
            setSelectedImage(post.picture);
        } catch (err) {
            console.log('Error fetching post:', err);
        }
    };

    const fetchGenres = async () => {
        try {
            const res = await axios.get('http://localhost:8081/genres');
            setGenres(res.data);
        } catch (err) {
            console.log('Error fetching genres:', err);
        }
    };

    const handleFileChange = (e) => {
        setSelectedImage(e.target.files[0]);
    };

    const handleUpdatePost = (e) => {
        e.preventDefault();

        if (!title || !description || !author || !price || !genre) {
            alert('Please fill all fields');
            return;
        }

        const formData = new FormData();
        formData.append('id', id);
        formData.append('title', title);
        formData.append('description', description);
        formData.append('author', author);
        formData.append('price', price);
        formData.append('genre', genre);
        if (selectedImage) {
            formData.append('picture', selectedImage);
        }

        axios.post('http://localhost:8081/edit-post', formData)
            .then(res => {
                if (res.data.message === 'Success') {
                    navigate(`/post/${id}`);
                } else {
                    console.log('Failed to update post');
                }
            })
            .catch(err => {
                console.log('Error occurred while updating post', err);
            });
    };

    return (
        <div className="create-post-container">
            <h2>Edit Post</h2>
            <form onSubmit={handleUpdatePost}>
                <input
                    type="text"
                    placeholder="Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    className="form-control mt-2"
                />
                <input
                    type="text"
                    placeholder="Author"
                    value={author}
                    onChange={(e) => setAuthor(e.target.value)}
                    className="form-control mt-2"
                />
                <textarea
                    placeholder="Write a caption..."
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    className="form-control mt-2"
                ></textarea>
                <input
                    type="number"
                    placeholder="Price"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                    className="form-control mt-2"
                />
                <select
                    value={genre}
                    onChange={(e) => setGenre(e.target.value)}
                    className="form-control mt-2"
                >
                    <option value="">Select Genre</option>
                    {genres.map((g) => (
                        <option key={g.id} value={g.name}>{g.name}</option>
                    ))}
                </select>
                <div className="image-upload-section">
                    <label htmlFor="file-input" className="image-upload-label">
                        Add or Drag & Drop an image
                    </label>
                    <input
                        id="file-input"
                        type="file"
                        onChange={handleFileChange}
                        className="form-control mt-2"
                        style={{ display: 'none' }}
                    />
                    <div
                        className="drop-zone"
                        onDragOver={(e) => e.preventDefault()}
                        onDrop={(e) => {
                            e.preventDefault();
                            setSelectedImage(e.dataTransfer.files[0]);
                        }}
                    >
                        {selectedImage ? (
                            <div className="image-preview">
                                <img
                                    src={URL.createObjectURL(selectedImage)}
                                    alt="Selected"
                                    className="selected-image"
                                />
                                <button
                                    type="button"
                                    className="remove-image-button"
                                    onClick={() => setSelectedImage(null)}
                                >
                                    Remove
                                </button>
                            </div>
                        ) : (
                            <p>Add or Drag & Drop an image</p>
                        )}
                    </div>
                </div>
                <button type="submit" className="btn btn-primary mt-2">Save Changes</button>
            </form>
        </div>
    );
}

export default EditPost;
