import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import FilterModal from './FilterModal';
import './Layout.css';

function Layout({ children }) {
  const [isFilterModalOpen, setIsFilterModalOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [filter, setFilter] = useState({ sortBy: 'newest', genre: '' });

  const openFilterModal = () => {
    setIsFilterModalOpen(true);
  };

  const closeFilterModal = () => {
    setIsFilterModalOpen(false);
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
    setFilter({ ...filter, searchTerm: query });
  };

  const handleFilterChange = (newFilter) => {
    setFilter(newFilter);
  };

  useEffect(() => {
    // Pass search and filter changes to children if necessary
  }, [searchQuery, filter]);

  return (
    <div className="layout">
      <header className="header">
        <Link to="/" className="logo">iSoulFeed</Link>
        <div className="search-filter">
          <input 
            type="text" 
            placeholder="Search..." 
            value={searchQuery} 
            onChange={(e) => handleSearch(e.target.value)} 
          />
          <button onClick={openFilterModal}>
            <i className="fas fa-filter"></i>
          </button>
        </div>
        <Link to="/addpost" className="add-post-btn">+ Add Post</Link>
      </header>
      <main>
        {isFilterModalOpen && (
          <FilterModal
            onClose={closeFilterModal}
            onFilterChange={handleFilterChange}
          />
        )}
        {React.cloneElement(children, { searchQuery, filter })}
      </main>
    </div>
  );
}

export default Layout;
