import React from 'react';
import axios from 'axios';
import './ConfirmDeleteModal.css';

function ConfirmDeleteModal({ isOpen, onClose, userEmail }) {
    if (!isOpen) return null;

    const handleDelete = async () => {
        try {
            const response = await axios.post('http://localhost:8081/deleteProfile', { email: userEmail });
            if (response.data === "Success") {
                alert("Profile deleted successfully");
                window.location.href = '/';
            } else {
                alert("Failed to delete profile");
            }
        } catch (error) {
            console.error("There was an error deleting the profile!", error);
            alert("Failed to delete profile");
        }
    };

    return (
        <div className="modal">
            <div className="modal-content">
                <span className="close" onClick={onClose}>&times;</span>
                <p>Are you sure you want to delete your profile? This action cannot be undone.</p>
                <div className="modal-buttons">
                    <button className="back-button" onClick={onClose}>Back</button>
                    <button className="delete-button" onClick={handleDelete}>Yes, I am sure</button>
                </div>
            </div>
        </div>
    );
}

export default ConfirmDeleteModal;
