    };

    return (
        <div className="chat-container">
            <div className="messages">
                {messages.map((msg, index) => (
                    <div key={index} className={msg.isMe ? 'message me' : 'message'}>
                        <p>{msg.content}</p>
                    </div>
                ))}
            </div>
