import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser } from '@fortawesome/free-solid-svg-icons';
import './Profile.css';

function Profile() {
    const [user, setUser] = useState(null);
    const [profilePic, setProfilePic] = useState(null);
    const [profilePicPath, setProfilePicPath] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const userData = localStorage.getItem('user');
        if (userData) {
            const parsedData = JSON.parse(userData);
            setUser(parsedData);
            setProfilePicPath(parsedData.profile_pic || '');
        } else {
            navigate('/'); // Redirect to login if user data is not found
        }
    }, [navigate]);

    const handleFileChange = (e) => {
        setProfilePic(e.target.files[0]);
    };

    const handleUploadProfilePic = () => {
        const formData = new FormData();
        formData.append('profilePic', profilePic);
        formData.append('email', user.email);

        axios.post('http://localhost:8081/upload-profile-pic', formData)
            .then(res => {
                if (res.data.message === "Success") {
                    setProfilePicPath(res.data.profilePicPath);
                    const updatedUser = { ...user, profile_pic: res.data.profilePicPath };
                    localStorage.setItem('user', JSON.stringify(updatedUser));
                    setUser(updatedUser);
                } else {
                    console.error("Failed to upload profile picture");
                }
            })
            .catch(err => {
                console.error("Error occurred while uploading profile picture", err);
            });
    };

    const handleLogout = () => {
        localStorage.removeItem('user');
        navigate('/');
    };

    if (!user) {
        return <div>Loading...</div>;
    }

    return (
        <div className="profile-container mt-5">
            <div className="profile-header d-flex justify-content-end">
                <button className="btn btn-secondary home-button" onClick={() => navigate('/home')}>
                    Home
                </button>
            </div>
            <div className="profile-card card mt-3">
                <div className="card-body">
                    <h3 className="card-title">
                        <FontAwesomeIcon icon={faUser} /> Profile
                    </h3>
                    <div className="profile-pic-container">
                        {profilePicPath && <img src={`http://localhost:8081/${profilePicPath}`} alt="Profile" className="profile-pic" />}
                        <input type="file" onChange={handleFileChange} className="form-control" />
                        <button className="btn btn-primary mt-2" onClick={handleUploadProfilePic}>Upload Profile Picture</button>
                    </div>
                    <p><strong>Name:</strong> {user.name}</p>
                    <p><strong>Email:</strong> {user.email}</p>
                    <hr />
                    <button className="btn btn-primary" onClick={() => navigate('/settings')}>Edit Profile</button>
                    <button className="btn btn-primary" onClick={() => alert('Publish Post clicked')}>Publish Post</button>
                    <button className="btn btn-danger" onClick={handleLogout}>Log Out</button>
                </div>
            </div>
        </div>
    );
}

export default Profile;
