import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Chat.css';

function Chat({ chatId, selectedChat }) {
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const user = JSON.parse(localStorage.getItem('user'));

    console.log('Chat component:', { chatId, selectedChat }); // Debugging line

    useEffect(() => {
        if (chatId) {
            axios.get(`http://localhost:8081/messages/${chatId}`)
                .then(res => {
                    setMessages(res.data);
                })
                .catch(err => {
                    console.error("Error fetching messages:", err);
                });
        }
    }, [chatId]);

    const handleSendMessage = () => {
        if (newMessage.trim()) {
            const messageData = {
                chat_id: chatId,
                user_email: user.email,
                message: newMessage,
            };
            axios.post('http://localhost:8081/messages', messageData)
                .then(res => {
                    setMessages([...messages, messageData]);
                    setNewMessage('');
                })
                .catch(err => {
                    console.error("Error sending message:", err);
                });
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-header">
                <img src={`http://localhost:8081/${selectedChat.profile_pic}`} alt={selectedChat.name} className="chat-profile-pic" />
                <p>{selectedChat.name}</p>
            </div>
            <div className="chat-messages">
                {messages.map((msg, index) => (
                    <div key={index} className={`chat-message ${msg.user_email === user.email ? 'sent' : 'received'}`}>
                        <p>{msg.message}</p>
                    </div>
                ))}
            </div>
            <div className="chat-input">
                <input 
                    type="text" 
                    value={newMessage} 
                    onChange={(e) => setNewMessage(e.target.value)} 
                    placeholder="Type your message..." 
                />
                <button onClick={handleSendMessage}>Send</button>
            </div>
        </div>
    );
}

export default Chat;
