import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Chat.css';
import { useParams } from 'react-router-dom';

const Chat = () => {
    const { id } = useParams();
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const [postDetails, setPostDetails] = useState(null);

    useEffect(() => {
        const fetchMessages = async () => {
            try {
                const res = await axios.get(`http://localhost:8081/messages/${id}`);
                setMessages(res.data.messages || []); // Ensure messages is always an array

                if (res.data.messages && res.data.messages.length > 0 && res.data.messages[0].post_id) {
                    const postRes = await axios.get(`http://localhost:8081/post/${res.data.messages[0].post_id}`);
                    setPostDetails(postRes.data);
                }
            } catch (error) {
                console.error('Error fetching messages:', error);
            }
        };
        fetchMessages();
    }, [id]);

    const handleSendMessage = async (e) => {
        e.preventDefault();
        if (!newMessage.trim()) return;

        try {
            await axios.post('http://localhost:8081/messages', {
                chat_id: id,
                user_email: localStorage.getItem('user'),
                message: newMessage
            });
            setNewMessage('');
            setMessages([...messages, { message: newMessage, sender: 'me' }]);
        } catch (error) {
            console.error('Error sending message:', error);
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-header">
                {postDetails && (
                    <div className="post-details">
                        <img src={`http://localhost:8081/${postDetails.picture}`} alt={postDetails.title} className="post-picture" />
                        <div>
                            <h3>{postDetails.title}</h3>
                            <p>{postDetails.author}</p>
                        </div>
                    </div>
                )}
            </div>
            <div className="chat-messages">
                {messages.map((msg, index) => (
                    <div key={index} className={`chat-message ${msg.sender === 'me' ? 'sent' : 'received'}`}>
                        <p>{msg.message}</p>
                    </div>
                ))}
            </div>
            <form onSubmit={handleSendMessage} className="chat-input">
                <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    placeholder="Type your message..."
                />
                <button type="submit">Send</button>
            </form>
        </div>
    );
};

export default Chat;
