import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './Chat.css';

function Chat() {
    const { chatId } = useParams();
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const [user, setUser] = useState(null);

    useEffect(() => {
        if (chatId) {
            axios.get(`http://localhost:8081/messages/${chatId}`)
                .then(res => {
                    setMessages(res.data);
                })
                .catch(err => {
                    console.error("Error fetching messages:", err);
                });
        }
    }, [chatId]);
    

    const handleSendMessage = () => {
        if (newMessage.trim() === '') return;

        const messageData = {
            chat_id: chatId,
            user_email: user.email,
            message: newMessage,
        };

        axios.post('http://localhost:8081/send-message', messageData)
            .then((res) => {
                if (res.data.message === "Success") {
                    setMessages([...messages, { ...messageData, timestamp: new Date() }]);
                    setNewMessage('');
                }
            })
            .catch((err) => {
                console.error("Error sending message:", err);
            });
    };

    return (
        <div className="chat-container">
            <h2>Chat</h2>
            <div className="chat-messages">
                {messages.map((msg, index) => (
                    <div key={index} className={`chat-message ${msg.user_email === user.email ? 'sent' : 'received'}`}>
                        <div className="chat-message-content">{msg.message}</div>
                    </div>
                ))}
            </div>
            <div className="chat-input">
                <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    placeholder="Type your message..."
                />
                <button onClick={handleSendMessage}>Send</button>
            </div>
        </div>
    );
}

export default Chat;
