import React, { useState } from 'react';
import './FilterModal.css';

const FilterModal = ({ show, handleClose, handleFilter }) => {
  const [genre, setGenre] = useState('');
  const [sort, setSort] = useState('');

  const applyFilter = () => {
    handleFilter({ genre, sort });
    handleClose();
  };

  if (!show) {
    return null;
  }

  return (
    <div className="filter-modal">
      <div className="filter-modal-content">
        <h3>Filter Options</h3>
        <div className="filter-option">
          <label htmlFor="sort">Sort By:</label>
          <select id="sort" value={sort} onChange={(e) => setSort(e.target.value)}>
            <option value="">Select</option>
            <option value="newest">Newest First</option>
            <option value="oldest">Oldest First</option>
            <option value="priceAsc">Price Ascending</option>
            <option value="priceDesc">Price Descending</option>
          </select>
        </div>
        <div className="filter-option">
          <label htmlFor="genre">Genre:</label>
          <select id="genre" value={genre} onChange={(e) => setGenre(e.target.value)}>
            <option value="">All Genres</option>
            <option value="Fiction">Fiction</option>
            <option value="Non-Fiction">Non-Fiction</option>
            <option value="Science Fiction">Science Fiction</option>
            <option value="Biography">Biography</option>
            <option value="Poetry">Poetry</option>
            <option value="Horror">Horror</option>
            <option value="Thriller">Thriller</option>
            <option value="Historical Fiction">Historical Fiction</option>
            <option value="Adventure">Adventure</option>
            <option value="Drama">Drama</option>
            <option value="Self-help">Self-help</option>
            <option value="Travel">Travel</option>
            <option value="Children's">Children's</option>
          </select>
        </div>
        <button className="filter-button" onClick={applyFilter}>Apply Filters</button>
        <button className="filter-button" onClick={handleClose}>Close</button>
      </div>
    </div>
  );
};

export default FilterModal;
