import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Chat.css';

const Chat = ({ match }) => {
    const chatId = match.params.chatId;
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState('');
    const [chatInfo, setChatInfo] = useState(null);

    useEffect(() => {
        const fetchMessages = async () => {
            const response = await axios.get(`http://localhost:8081/messages/${chatId}`);
            setMessages(response.data);
        };
        const fetchChatInfo = async () => {
            const response = await axios.get(`http://localhost:8081/chats/${chatId}`);
            setChatInfo(response.data);
        };
        fetchMessages();
        fetchChatInfo();
    }, [chatId]);

    const handleSendMessage = async () => {
        if (message.trim() === '') return;

        const newMessage = {
            chat_id: chatId,
            user_email: chatInfo.user_email,
            message: message,
            postImage: chatInfo.postImage,
            postTitle: chatInfo.postTitle,
        };

        await axios.post('http://localhost:8081/messages', newMessage);
        setMessages([...messages, newMessage]);
        setMessage('');
    };

    return (
        <div className="chat-container">
            <div className="chat-header">
                <img src={`http://localhost:8081/${chatInfo?.profile_pic}`} alt="Profile" className="profile-pic" />
                <div className="chat-user-name">{chatInfo?.name}</div>
            </div>
            <div className="messages-list">
                {messages.map((msg, index) => (
                    <div key={index} className="message-item">
                        {msg.postImage && (
                            <div className="post-image-container">
                                <img src={`http://localhost:8081/${msg.postImage}`} alt="Post" className="post-image" />
                                <div className="post-title">{msg.postTitle}</div>
                            </div>
                        )}
                        <div className="message-content">
                            <span>{msg.message}</span>
                            <span className="message-timestamp">{new Date(msg.timestamp).toLocaleString()}</span>
                        </div>
                    </div>
                ))}
            </div>
            <div className="chat-input-container">
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Type your message..."
                    className="chat-input"
                />
                <button onClick={handleSendMessage} className="send-button">Send</button>
            </div>
        </div>
    );
};

export default Chat;
