import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Post from './Post';
import './Home.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faFilter } from '@fortawesome/free-solid-svg-icons';

function Home() {
    const [posts, setPosts] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [priceFilter, setPriceFilter] = useState('');
    const [genreFilter, setGenreFilter] = useState('');
    const [sortOption, setSortOption] = useState('');
    const [isFilterOpen, setIsFilterOpen] = useState(false);

    useEffect(() => {
        fetchPosts();
    }, [searchTerm, priceFilter, genreFilter, sortOption]);

    const fetchPosts = () => {
        axios.get('http://localhost:8081/posts', {
            params: {
                searchTerm,
                price: priceFilter,
                genre: genreFilter,
                sort: sortOption,
            }
        })
        .then(res => {
            setPosts(res.data);
        })
        .catch(err => console.log(err));
    };

    const handleSearchChange = (e) => setSearchTerm(e.target.value);
    const handlePriceChange = (e) => setPriceFilter(e.target.value);
    const handleGenreChange = (e) => setGenreFilter(e.target.value);
    const handleSortChange = (e) => setSortOption(e.target.value);

    const toggleFilterMenu = () => setIsFilterOpen(!isFilterOpen);

    return (
        <div className="home">
            <h1>All Posts</h1>
            <div className="search-bar">
                <input
                    type="text"
                    placeholder="Search..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                />
                <button onClick={fetchPosts}>
                    <FontAwesomeIcon icon={faSearch} />
                </button>
                <button onClick={toggleFilterMenu}>
                    <FontAwesomeIcon icon={faFilter} />
                </button>
            </div>
            {isFilterOpen && (
                <div className="filter-menu">
                    <select value={sortOption} onChange={handleSortChange}>
                        <option value="">Sort By</option>
                        <option value="newest">Newest First</option>
                        <option value="oldest">Oldest First</option>
                        <option value="priceAsc">Price Ascending</option>
                        <option value="priceDesc">Price Descending</option>
                    </select>
                    <select value={genreFilter} onChange={handleGenreChange}>
                        <option value="">Select Genre</option>
                        <option value="Fiction">Fiction</option>
                        <option value="Non-Fiction">Non-Fiction</option>
                        <option value="Science Fiction">Science Fiction</option>
                        <option value="Biography">Biography</option>
                        <option value="Poetry">Poetry</option>
                        <option value="Fantasy">Fantasy</option>
                        <option value="Mystery">Mystery</option>
                        <option value="Romance">Romance</option>
                        <option value="Horror">Horror</option>
                        <option value="Thriller">Thriller</option>
                        <option value="Historical Fiction">Historical Fiction</option>
                        <option value="Adventure">Adventure</option>
                        <option value="Drama">Drama</option>
                        <option value="Self-help">Self-help</option>
                        <option value="Travel">Travel</option>
                        <option value="Children's">Children's</option>
                    </select>
                </div>
            )}
            <div className="posts">
                {posts.map(post => (
                    <Post key={post.id} post={post} />
                ))}
            </div>
        </div>
    );
}

export default Home;
