import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './Chat.css';

function Chat() {
    const { chatId } = useParams();
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const user = JSON.parse(localStorage.getItem('user'));

    useEffect(() => {
        fetchMessages();
    }, [chatId]);

    const fetchMessages = async () => {
        try {
            const res = await axios.get(`http://localhost:8081/messages?chat_id=${chatId}`);
            setMessages(res.data);
        } catch (err) {
            console.error('Error fetching messages', err);
        }
    };

    const handleSendMessage = async (e) => {
        e.preventDefault();
        if (!newMessage.trim()) return;

        try {
            await axios.post('http://localhost:8081/messages', {
                chat_id: chatId,
                user_email: user.email,
                message: newMessage
            });
            setNewMessage('');
            fetchMessages(); // Refresh messages after sending
        } catch (err) {
            console.error('Error sending message', err);
        }
    };

    return (
        <div className="chat-container">
            <h2 className="chat-header">Chat</h2>
            <div className="messages-container">
                {messages.map((message, index) => (
                    <div key={index} className={`message ${message.user_email === user.email ? 'message-sent' : 'message-received'}`}>
                        {message.message}
                    </div>
                ))}
            </div>
            <form className="send-message-form" onSubmit={handleSendMessage}>
                <input
                    type="text"
                    className="send-message-input"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    placeholder="Type your message..."
                />
                <button type="submit" className="send-message-button">Send</button>
            </form>
        </div>
    );
}

export default Chat;
