import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './PostDetail.css';
import OwnPostDetail from './OwnPostDetail';

function PostDetail() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [post, setPost] = useState(null);
    const [user, setUser] = useState(null);

    useEffect(() => {
        const userData = localStorage.getItem('user');
        if (userData) {
            setUser(JSON.parse(userData));
        }
        fetchPost();
    }, []);

    const fetchPost = () => {
        axios.get(`http://localhost:8081/post/${id}`)
            .then(res => {
                const fetchedPost = res.data;
                setPost(fetchedPost);

                if (fetchedPost.user_id === user?.id) {
                    navigate(`/own-post/${id}`);
                }
            })
            .catch(err => {
                console.log("Error fetching post:", err);
            });
    };

    if (!post) {
        return <div>Loading...</div>;
    }

    return (
        <div className="post-detail-container">
            <div className="post-header">
                <h1>{post.title}</h1>
                <p>by {post.author}</p>
            </div>
            <div className="post-content">
                <img src={`http://localhost:8081/${post.picture}`} alt={post.title} />
                <p className="post-description">{post.description}</p>
                <p className="post-price">Price: â‚¬{post.price}</p>
                <p className="post-user">Posted by: {post.user_name}</p>
            </div>
        </div>
    );
}

export default PostDetail;
