import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';
import './PostDetail.css';

function OwnPostDetail() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [post, setPost] = useState(null);

    useEffect(() => {
        fetchPost();
    }, []);

    const fetchPost = () => {
        console.log(`Fetching post with id: ${id}`);
        axios.get(`http://localhost:8081/post/${id}`)
            .then(res => {
                console.log("Post data received:", res.data);
                setPost(res.data);
            })
            .catch(err => {
                console.error("Error fetching post:", err);
            });
    };

    const handleEditPost = () => {
        navigate(`/edit-post/${id}`);
    };

    const handleDeletePost = () => {
        axios.post(`http://localhost:8081/delete-post`, { id })
            .then(res => {
                if (res.data.message === "Success") {
                    navigate('/home');
                } else {
                    console.log("Failed to delete post");
                }
            })
            .catch(err => {
                console.error("Error deleting post:", err);
            });
    };

    if (!post) {
        return <div>Loading...</div>;
    }

    return (
        <div className="post-detail-container">
            <button className="back-button" onClick={() => navigate('/home')}>Back to Home</button>
            <div className="post-header">
                <h1 className="post-title">{post.title}</h1>
            </div>
            <div className="post-content">
                <div className="post-image-section">
                    <img src={`http://localhost:8081/${post.picture}`} alt={post.title} className="post-image-large" />
                </div>
                <div className="post-info-section">
                    <p className="post-price">Price: â‚¬{parseFloat(post.price).toFixed(2)}</p>
                    <p className="post-description">{post.description}</p>
                    <p className="post-user">Posted by: You</p>
                    <div className="post-actions">
                        <FontAwesomeIcon icon={faEdit} onClick={handleEditPost} />
                        <FontAwesomeIcon icon={faTrash} onClick={handleDeletePost} />
                    </div>
                </div>
            </div>
        </div>
    );
}

export default OwnPostDetail;
