import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './Inbox.css';

function Inbox() {
    const [inbox, setInbox] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const userData = localStorage.getItem('user');
        if (!userData) {
            navigate('/');
            return;
        }

        const user = JSON.parse(userData);
        console.log("Fetching inbox for user ID:", user.id);

        axios.get(`http://localhost:8081/inbox/${user.id}`)
            .then((res) => {
                console.log("Fetched inbox data:", res.data);
                setInbox(res.data);
            })
            .catch((err) => {
                console.error("Error fetching messages:", err);
            });
    }, [navigate]);

    return (
        <div className="inbox-container">
            <h2>Inbox</h2>
            <div className="inbox-list">
                {inbox.map((chat, index) => (
                    <Link key={index} to={`/chat/${chat.chat_id}`} className="inbox-item">
                        {chat.name}
                    </Link>
                ))}
            </div>
        </div>
    );
}

export default Inbox;
