import React from 'react';
import './Post.css';

function Post({ post, onClick }) {
    return (
        <div className="post-card" onClick={onClick}>
            <img src={`http://localhost:8081/${post.picture}`} alt={post.title} className="post-image" />
            <div className="post-content">
                <h2>{post.title}</h2>
                <p><strong>Author:</strong> {post.author}</p>
                <p><strong>Posted by:</strong> {post.user_name}</p>
                <p><strong>Price:</strong> â‚¬{post.price}</p>
                <p><strong>Genre:</strong> {post.genre}</p>
            </div>
        </div>
    );
}

export default Post;
