import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const EditPost = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState('');
    const [author, setAuthor] = useState('');
    const [selectedFile, setSelectedFile] = useState(null);

    useEffect(() => {
        axios.get(`http://localhost:8081/edit-post/${id}`)
            .then((response) => {
                const post = response.data;
                setTitle(post.title);
                setDescription(post.description);
                setPrice(post.price);
                setAuthor(post.author);
            })
            .catch((error) => {
                console.error("Error fetching post data:", error);
            });
    }, [id]);

    const handleFileChange = (event) => {
        setSelectedFile(event.target.files[0]);
    };

    const handleUpdatePost = () => {
        const formData = new FormData();
        formData.append('id', id);
        formData.append('title', title);
        formData.append('description', description);
        formData.append('price', price);
        formData.append('author', author);
        if (selectedFile) {
            formData.append('picture', selectedFile);
        }

        axios.post('http://localhost:8081/edit-post', formData)
            .then((res) => {
                if (res.data.message === "Success") {
                    navigate(`/post/${id}`);
                } else {
                    console.error("Failed to update post:", res.data.message);
                }
            })
            .catch((err) => {
                console.error("Error occurred while updating post", err);
            });
    };

    return (
        <div>
            <h1>Edit Post</h1>
            <form onSubmit={(e) => { e.preventDefault(); handleUpdatePost(); }}>
                <div>
                    <label>Title:</label>
                    <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
                </div>
                <div>
                    <label>Description:</label>
                    <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
                </div>
                <div>
                    <label>Price:</label>
                    <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} />
                </div>
                <div>
                    <label>Author:</label>
                    <input type="text" value={author} onChange={(e) => setAuthor(e.target.value)} />
                </div>
                <div>
                    <label>Picture:</label>
                    <input type="file" onChange={handleFileChange} />
                </div>
                <button type="submit">Update Post</button>
            </form>
        </div>
    );
};

export default EditPost;
