import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Home.css';
import FilterModal from './FilterModal';

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [showFilterModal, setShowFilterModal] = useState(false);
  const [filters, setFilters] = useState({ genre: '', sort: '' });

  useEffect(() => {
    fetchPosts();
  }, [filters]);

  const fetchPosts = async () => {
    try {
      const response = await axios.get('/posts', {
        params: {
          searchTerm,
          genre: filters.genre,
          sort: filters.sort,
        },
      });
      setPosts(response.data);
    } catch (error) {
      console.error('Error fetching posts:', error);
    }
  };

  const handleFilter = (newFilters) => {
    setFilters(newFilters);
  };

  return (
    <div className="home-container">
      <div className="search-filter">
        <input
          type="text"
          placeholder="Search..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="filter-button" onClick={() => setShowFilterModal(true)}>
          <i className="fas fa-filter"></i>
        </button>
        <button className="search-button" onClick={fetchPosts}>
          <i className="fas fa-search"></i>
        </button>
      </div>
      <FilterModal
        show={showFilterModal}
        handleClose={() => setShowFilterModal(false)}
        handleFilter={handleFilter}
      />
      <div className="posts-container">
        {posts.length > 0 ? (
          posts.map((post) => (
            <div key={post.id} className="post-card">
              <img src={post.imageUrl} alt={post.title} className="post-image" />
              <div className="post-details">
                <a href={`/post/${post.id}`} className="post-title">{post.title}</a>
                <p className="post-author"><strong>Author:</strong> {post.author}</p>
                <p className="post-user"><strong>Posted by:</strong> {post.userName}</p>
                <p className="post-price"><strong>Price:</strong> â‚¬{post.price}</p>
                <p className="post-genre"><strong>Genre:</strong> {post.genre}</p>
              </div>
            </div>
          ))
        ) : (
          <p>No posts found</p>
        )}
      </div>
    </div>
  );
};

export default Home;
