import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FilterModal.css';

const FilterModal = ({ onClose, onFilter }) => {
    const [genres, setGenres] = useState([]);
    const [selectedGenre, setSelectedGenre] = useState('');

    useEffect(() => {
        axios.get('http://localhost:8081/genres')
            .then(res => {
                setGenres(res.data);
            })
            .catch(err => {
                console.error("Error fetching genres:", err);
            });
    }, []);

    const handleFilter = () => {
        onFilter({ genre: selectedGenre });
        onClose();
    };

    return (
        <>
            <div className="filter-modal-backdrop" onClick={onClose}></div>
            <div className="filter-modal">
                <button className="filter-modal-close" onClick={onClose}>Ã—</button>
                <h2>Filter Posts</h2>
                <select value={selectedGenre} onChange={e => setSelectedGenre(e.target.value)}>
                    <option value="">All Genres</option>
                    {genres.map(genre => (
                        <option key={genre.id} value={genre.name}>{genre.name}</option>
                    ))}
                </select>
                <button onClick={handleFilter}>Apply Filters</button>
            </div>
        </>
    );
};

export default FilterModal;
