import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Layout from './Layout';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp, faThumbsDown } from '@fortawesome/free-solid-svg-icons';
import './Home.css';

const Home = () => {
    const [posts, setPosts] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [filter, setFilter] = useState('');

    useEffect(() => {
        fetchPosts();
    }, [searchQuery, filter]);

    const fetchPosts = async () => {
        try {
            const response = await axios.get('http://localhost:8081/posts', {
                params: {
                    search: searchQuery,
                    filter: filter
                }
            });
            setPosts(response.data);
        } catch (error) {
            console.error('Error fetching posts:', error);
        }
    };

    const handleSearch = (query) => {
        setSearchQuery(query);
    };

    const handleFilterChange = (value) => {
        setFilter(value);
    };

    return (
        <Layout onSearch={handleSearch} onFilterChange={handleFilterChange}>
            <div className="posts-container">
                {posts.map(post => (
                    <div className="post" key={post.id}>
                        <img src={`http://localhost:8081/${post.picture}`} alt={post.title} className="post-image" />
                        <div className="post-details">
                            <div className="post-title">{post.title}</div>
                            <div className="post-description">{post.description}</div>
                            <div className="post-user">Posted by: {post.email}</div>
                            <div className="post-price">Price: â‚¬{post.price}</div>
                        </div>
                        <div className="post-actions">
                            <div className="likes">
                                <FontAwesomeIcon icon={faThumbsUp} /> {post.likes}
                            </div>
                            <div className="dislikes">
                                <FontAwesomeIcon icon={faThumbsDown} /> {post.dislikes}
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </Layout>
    );
};

export default Home;
