import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './PostDetail.css';

const PostDetail = () => {
    const { id } = useParams();
    const [post, setPost] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [message, setMessage] = useState('');
    const [sellerId, setSellerId] = useState(null);

    useEffect(() => {
        const fetchPost = async () => {
            try {
                const res = await axios.get(`http://localhost:8081/post/${id}`);
                setPost(res.data);
                setSellerId(res.data.user_id); // Ensure you get the seller's user ID
            } catch (error) {
                console.error('Error fetching post:', error);
            }
        };
        fetchPost();
    }, [id]);

    const handleContactSeller = () => {
        setShowModal(true);
    };

    const handleSendMessage = async () => {
        if (message.trim() === '') return;
        try {
            const buyerId = JSON.parse(localStorage.getItem('user')).id;
            const response = await axios.post('http://localhost:8081/createChat', {
                buyer_id: buyerId,
                seller_id: sellerId
            });

            const chatId = response.data.chat_id;
            await axios.post('http://localhost:8081/messages', {
                chat_id: chatId,
                user_email: localStorage.getItem('user_email'),
                message,
                post_id: id // Include post ID for reference
            });

            setMessage('');
            setShowModal(false);
            window.location.href = `/inbox/${chatId}`; // Redirect to the chat
        } catch (error) {
            console.error('Error sending message:', error);
        }
    };

    if (!post) {
        return <div>Loading...</div>;
    }

    return (
        <div className="post-detail">
            <img src={`http://localhost:8081/${post.picture}`} alt={post.title} className="post-picture" />
            <h1>{post.title}</h1>
            <h2>by {post.author}</h2>
            <p>Price: â‚¬{post.price}</p>
            <p>{post.description}</p>
            <p>Posted by: {post.user_name}</p>
            <button onClick={handleContactSeller} className="contact-seller-button">Contact Seller</button>

            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <span className="close" onClick={() => setShowModal(false)}>&times;</span>
                        <h2>Contact Seller</h2>
                        <input
                            type="text"
                            placeholder="Type your message..."
                            value={message}
                            onChange={(e) => setMessage(e.target.value)}
                        />
                        <button onClick={handleSendMessage}>Send</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default PostDetail;
