import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faCog } from '@fortawesome/free-solid-svg-icons';

function Home() {
    const [user, setUser] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const userData = localStorage.getItem('user');
        if (userData) {
            setUser(JSON.parse(userData));
        } else {
            navigate('/');
        }
    }, [navigate]);

    if (!user) {
        return <div>Loading...</div>;
    }

    return (
        <div className="home-container">
            <header className="home-header">
                <div className="welcome-message">
                    <h1>Welcome, {user.name}!</h1>
                    <p>We're glad to see you again.</p>
                </div>
                <div className="action-buttons">
                    <button className="btn btn-primary" onClick={() => navigate('/profile')}>
                        <FontAwesomeIcon icon={faUser} className="icon-spacing" /> Profile
                    </button>
                    <button className="btn btn-secondary" onClick={() => navigate('/settings')}>
                        <FontAwesomeIcon icon={faCog} className="icon-spacing" /> Settings
                    </button>
                </div>
            </header>
            <section className="activity-section">
                <h2>Recent Activity</h2>
                <ul>
                    <li>Logged in from a new device</li>
                    <li>Changed password</li>
                    <li>Updated profile picture</li>
                </ul>
            </section>
            <footer className="home-footer">
                <p>Â© 2024 Neverland. All rights reserved.</p>
                <div className="footer-links">
                    <a href="/terms-of-service">Terms of Service</a>
                    <a href="/privacy-policy">Privacy Policy</a>
                    <a href="/contact-us">Contact Us</a>
                </div>
            </footer>
        </div>
    );
}

export default Home;
