import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Layout from './Layout';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp, faThumbsDown } from '@fortawesome/free-solid-svg-icons';
import './Home.css';

function Home() {
    const [posts, setPosts] = useState([]);
    const [filter, setFilter] = useState({
        query: '',
        sortBy: '',
        genre: '',
    });
    const [genres, setGenres] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        fetchPosts();
        fetchGenres();
    }, []);

    const fetchPosts = () => {
        axios.get('http://localhost:8081/all-posts')
            .then(res => {
                if (Array.isArray(res.data)) {
                    setPosts(res.data);
                } else {
                    setPosts([]);
                }
            })
            .catch(err => {
                setPosts([]);
            });
    };

    const fetchGenres = () => {
        axios.get('http://localhost:8081/genres')
            .then(res => {
                setGenres(res.data);
            })
            .catch(err => {
                console.log(err);
            });
    };

    const handleSearch = (query) => {
        setFilter({ ...filter, query });
        if (query.trim() === '') {
            fetchPosts();
        } else {
            axios.get('http://localhost:8081/search-posts', {
                params: { q: query }
            })
            .then(res => {
                if (Array.isArray(res.data)) {
                    setPosts(res.data);
                } else {
                    setPosts([]);
                }
            })
            .catch(err => {
                setPosts([]);
            });
        }
    };

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilter({ ...filter, [name]: value });
    };

    const applyFilters = () => {
        axios.get('http://localhost:8081/filter-posts', {
            params: { ...filter }
        })
        .then(res => {
            if (Array.isArray(res.data)) {
                setPosts(res.data);
            } else {
                setPosts([]);
            }
        })
        .catch(err => {
            setPosts([]);
        });
    };

    useEffect(() => {
        applyFilters();
    }, [filter]);

    const handlePostClick = (id) => {
        navigate(`/post/${id}`);
    };

    return (
        <Layout onSearch={handleSearch}>
            <div className="home-container">
                <h2>All Posts</h2>
                <div className="filter-container">
                    <input
                        type="text"
                        name="query"
                        placeholder="Search..."
                        value={filter.query}
                        onChange={handleFilterChange}
                        className="form-control"
                    />
                    <select
                        name="sortBy"
                        value={filter.sortBy}
                        onChange={handleFilterChange}
                        className="form-control"
                    >
                        <option value="">Sort By</option>
                        <option value="date">Date</option>
                        <option value="price">Price</option>
                        <option value="author">Author</option>
                    </select>
                    <select
                        name="genre"
                        value={filter.genre}
                        onChange={handleFilterChange}
                        className="form-control"
                    >
                        <option value="">Select Genre</option>
                        {genres.map(genre => (
                            <option key={genre.id} value={genre.name}>{genre.name}</option>
                        ))}
                    </select>
                </div>
                <div className="posts-container">
                    {Array.isArray(posts) && posts.length > 0 ? (
                        posts.map(post => (
                            <div className="post" key={post.id} onClick={() => handlePostClick(post.id)}>
                                <img src={`http://localhost:8081/${post.picture || 'uploads/1719331628307.png'}`} alt={post.title} className="post-image" />
                                <div className="post-details">
                                    <p className="post-title">{post.title}</p>
                                    <p className="post-description">{post.description}</p>
                                    {post.price !== undefined && post.price !== null ? (
                                        <p className="post-price">Price: â‚¬{parseFloat(post.price).toFixed(2)}</p>
                                    ) : (
                                        <p className="post-price">Price: N/A</p>
                                    )}
                                    <p className="post-user">Posted by: {post.user_name || 'Unknown'}</p>
                                </div>
                                <div className="post-actions">
                                    <span className="likes"><FontAwesomeIcon icon={faThumbsUp} /> 10</span>
                                    <span className="dislikes"><FontAwesomeIcon icon={faThumbsDown} /> 2</span>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p className="no-posts">No posts available.</p>
                    )}
                </div>
            </div>
        </Layout>
    );
}

export default Home;
