import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import './Inbox.css';

function Inbox() {
    const [chats, setChats] = useState([]);
    const navigate = useNavigate();
    const { email } = useParams();

    useEffect(() => {
        fetchChats();
    }, [email]);

    const fetchChats = async () => {
        try {
            const response = await axios.get(`http://localhost:8081/inbox/${email}`);
            console.log('Fetched chats:', response.data);  // Debug log
            setChats(response.data);
        } catch (error) {
            console.error('Failed to fetch chats:', error);  // Debug log
        }
    };

    const openChat = (chatId) => {
        navigate(`/chat/${chatId}`);
    };

    return (
        <div className="inbox-container">
            <h2>Inbox</h2>
            {chats.length === 0 ? (
                <p>No chats available.</p>
            ) : (
                chats.map((chat, index) => (
                    <div key={index} className="chat-item" onClick={() => openChat(chat.chat_id)}>
                        <img src={`http://localhost:8081/${chat.profile_pic}`} alt={chat.name} className="profile-pic" />
                        <div className="chat-info">
                            <span className="chat-name">{chat.name}</span>
                        </div>
                    </div>
                ))
            )}
        </div>
    );
}

export default Inbox;
