import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Chat.css';
import { useParams } from 'react-router-dom';

function Chat() {
    const { id } = useParams();
    const [messages, setMessages] = useState([]);
    const user = JSON.parse(localStorage.getItem('user'));

    useEffect(() => {
        axios.get(`http://localhost:8081/chat/${id}`)
            .then(res => {
                setMessages(res.data);
            })
            .catch(err => {
                console.error("Error fetching chat:", err);
            });
    }, [id]);

    const handleSendMessage = (e) => {
        e.preventDefault();
        const messageContent = e.target.elements.message.value;
        axios.post(`http://localhost:8081/sendMessage`, {
            chat_id: id,
            user_email: user.email,
            message: messageContent,
        })
            .then(res => {
                setMessages([...messages, res.data]);
            })
            .catch(err => {
                console.error("Error sending message:", err);
            });
        e.target.elements.message.value = '';
    };

    return (
        <div className="chat-container">
            <div className="chat-messages">
                {messages.map((message, index) => (
                    <div key={index} className={`message ${message.user_email === user.email ? 'sent' : 'received'}`}>
                        <p>{message.message}</p>
                    </div>
                ))}
            </div>
            <form onSubmit={handleSendMessage} className="message-form">
                <input type="text" name="message" placeholder="Type a message..." required />
                <button type="submit">Send</button>
            </form>
        </div>
    );
}

export default Chat;
