import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FilterModal.css';

function FilterModal({ show, onClose, onFilterChange }) {
  const [genres, setGenres] = useState([]);
  const [sortBy, setSortBy] = useState('');
  const [genre, setGenre] = useState('');

  useEffect(() => {
    fetchGenres();
  }, []);

  const fetchGenres = async () => {
    try {
      const res = await axios.get('http://localhost:8081/genres');
      setGenres(res.data);
    } catch (err) {
      console.log("Error fetching genres:", err);
    }
  };

  const handleApplyFilters = () => {
    onFilterChange({ sortBy, genre });
    onClose();
  };

  if (!show) {
    return null;
  }

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Filter Options</h2>
        <div className="filter-group">
          <label>Sort By:</label>
          <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
            <option value="">Select</option>
            <option value="newest">Newest</option>
            <option value="oldest">Oldest</option>
            <option value="priceAsc">Price: Low to High</option>
            <option value="priceDesc">Price: High to Low</option>
          </select>
        </div>
        <div className="filter-group">
          <label>Genre:</label>
          <select value={genre} onChange={(e) => setGenre(e.target.value)}>
            <option value="">All Genres</option>
            {genres.map(genre => (
              <option key={genre.id} value={genre.name}>{genre.name}</option>
            ))}
          </select>
        </div>
        <div className="modal-actions">
          <button className="btn btn-primary" onClick={handleApplyFilters}>Apply</button>
          <button className="btn btn-secondary" onClick={onClose}>Close</button>
        </div>
      </div>
    </div>
  );
}

export default FilterModal;
