import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

function CreatePost() {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState('');
    const [author, setAuthor] = useState(''); // Add this line
    const [picture, setPicture] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [postId, setPostId] = useState(null);
    const location = useLocation();
    const navigate = useNavigate();

    useEffect(() => {
        if (location.state && location.state.post) {
            const post = location.state.post;
            setTitle(post.title);
            setDescription(post.description);
            setPrice(post.price);
            setAuthor(post.author); // Add this line
            setPostId(post.id);
            setIsEditing(true);
        }
    }, [location.state]);

    const handleSubmit = (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('title', title);
        formData.append('description', description);
        formData.append('price', price);
        formData.append('author', author); // Add this line
        formData.append('picture', picture);
        if (isEditing) {
            formData.append('id', postId);
            axios.post('http://localhost:8081/edit-post', formData)
                .then(res => {
                    if (res.data.message === "Success") {
                        navigate('/home');
                    } else {
                        alert("Failed to edit post");
                    }
                })
                .catch(err => {
                    console.error("Error editing post:", err);
                    alert("Error editing post");
                });
        } else {
            axios.post('http://localhost:8081/create-post', formData)
                .then(res => {
                    if (res.data.message === "Success") {
                        navigate('/home');
                    } else {
                        alert("Failed to create post");
                    }
                })
                .catch(err => {
                    console.error("Error creating post:", err);
                    alert("Error creating post");
                });
        }
    };

    return (
        <div className="create-post-container">
            <h2>{isEditing ? 'Edit Post' : 'Create Post'}</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                />
                <textarea
                    placeholder="Write a caption..."
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    required
                />
                <input
                    type="number"
                    placeholder="Price"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Author"
                    value={author}
                    onChange={(e) => setAuthor(e.target.value)} // Add this line
                    required
                />
                <input
                    type="file"
                    onChange={(e) => setPicture(e.target.files[0])}
                    required={!isEditing} // Picture is required only when creating a new post
                />
                <button type="submit">{isEditing ? 'Update Post' : 'Share Post'}</button>
            </form>
        </div>
    );
}

export default CreatePost;
