import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Layout from './Layout';
import './Home.css';

const Home = () => {
    const [posts, setPosts] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [filters, setFilters] = useState({
        sortBy: '',
        genre: '',
    });

    useEffect(() => {
        fetchPosts();
    }, [searchQuery, filters]);

    const fetchPosts = async () => {
        try {
            const response = await axios.get('http://localhost:8081/posts', {
                params: {
                    search: searchQuery,
                    ...filters,
                },
            });
            setPosts(response.data);
        } catch (error) {
            console.error('Error fetching posts:', error);
        }
    };

    const handleSearch = (query) => {
        setSearchQuery(query);
    };

    const handleFilterChange = (newFilters) => {
        setFilters(newFilters);
    };

    return (
        <Layout onSearch={handleSearch} onFilterChange={handleFilterChange}>
            <div className="posts-container">
                {posts.map(post => (
                    <div className="post" key={post.id}>
                        <img src={`http://localhost:8081/${post.picture}`} alt={post.title} className="post-image" />
                        <div className="post-details">
                            <div className="post-title">{post.title}</div>
                            <div className="post-description">{post.description}</div>
                            <div className="post-user">Posted by: {post.email}</div>
                            <div className="post-price">Price: â‚¬{post.price}</div>
                            <div className="post-genre">Genre: {post.genre}</div>
                        </div>
                    </div>
                ))}
            </div>
        </Layout>
    );
};

export default Home;
