import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import './CreatePost.css';

function CreatePost() {
    const navigate = useNavigate();
    const location = useLocation();
    const { user, editingPost } = location.state || {};
    const [selectedImage, setSelectedImage] = useState(null);
    const [description, setDescription] = useState(editingPost ? editingPost.description : '');
    const [title, setTitle] = useState(editingPost ? editingPost.title : '');
    const [price, setPrice] = useState(editingPost ? editingPost.price : '');
    const [showDiscardModal, setShowDiscardModal] = useState(false);

    useEffect(() => {
        if (!user) {
            navigate('/profile');
        }
    }, [user, navigate]);

    const handleImageChange = (e) => {
        setSelectedImage(e.target.files[0]);
    };

    const handleRemoveImage = () => {
        setSelectedImage(null);
    };

    const handleSharePost = () => {
        if (!user) return;

        const formData = new FormData();
        formData.append('image', selectedImage);
        formData.append('title', title);
        formData.append('description', description);
        formData.append('price', price);
        formData.append('email', user.email);

        axios.post('http://localhost:8081/create-post', formData)
            .then(res => {
                if (res.data.message === "Success") {
                    navigate('/profile');
                } else {
                    console.log("Failed to create post");
                }
            })
            .catch(err => {
                console.log("Error occurred while creating post", err);
            });
    };

    const handleCancelPost = () => {
        setShowDiscardModal(true);
    };

    const handleDiscardPost = () => {
        navigate('/profile');
    };

    return (
        <div className="create-post-container">
            <h2>{editingPost ? 'Edit Post' : 'Publish a Post'}</h2>
            <input
                type="text"
                placeholder="Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                className="form-control mt-2"
            />
            <textarea
                placeholder="Write a caption..."
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                className="form-control mt-2"
            ></textarea>
            <div className="price-input-container">
                <input
                    type="text"
                    placeholder="Price"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                    className="form-control mt-2 price-input"
                />
                <span className="currency-symbol">â‚¬</span>
            </div>
            <div className="image-upload-section">
                <label htmlFor="file-input" className="image-upload-label">
                    {selectedImage ? 'Change Image' : 'Add or Drag & Drop an image'}
                </label>
                <input
                    id="file-input"
                    type="file"
                    onChange={handleImageChange}
                    className="form-control mt-2"
                    style={{ display: 'none' }}
                />
                {selectedImage && (
                    <div className="image-preview-container">
                        <div className="image-preview">
                            <img
                                src={URL.createObjectURL(selectedImage)}
                                alt="Selected"
                                className="selected-image"
                            />
                            <button
                                className="remove-image-button"
                                onClick={handleRemoveImage}
                            >
                                &times;
                            </button>
                        </div>
                    </div>
                )}
            </div>
            <button className="btn btn-primary mt-2" onClick={handleSharePost}>Share</button>
            <button className="btn btn-secondary mt-2" onClick={handleCancelPost}>Cancel</button>

            {showDiscardModal && (
                <div className="modal">
                    <div className="modal-content">
                        <p>Discard post?</p>
                        <p>If you leave, your edits won't be saved.</p>
                        <button className="btn btn-danger mt-2" onClick={handleDiscardPost}>Discard</button>
                        <button className="btn btn-secondary mt-2" onClick={() => setShowDiscardModal(false)}>Cancel</button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default CreatePost;
