import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Validation from './SignupValidation';
import axios from 'axios';

function Signup() {
    const [values, setValues] = useState({
        name: '',
        email: '',
        password: ''
    });
    const [errors, setErrors] = useState({});
    const [errorMessage, setErrorMessage] = useState(""); // Add this state
    const [agree, setAgree] = useState(false); // State for terms and conditions
    const navigate = useNavigate();

    const handleInput = (event) => {
        setValues(prev => ({ ...prev, [event.target.name]: event.target.value }));
    };

    const handleCheckbox = (event) => {
        setAgree(event.target.checked);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const err = Validation(values);
        setErrors(err);

        if (!agree) {
            setErrorMessage("You must agree to the terms and conditions");
            return;
        }

        if (err.name === "" && err.email === "" && err.password === "") {
            axios.post('http://localhost:8081/signup', values)
                .then(res => {
                    if (res.data !== "Failed" && res.data !== "Error") {
                        localStorage.setItem('user', JSON.stringify(res.data));
                        navigate('/home');
                    } else {
                        setErrorMessage("Failed to create account"); // Set error message
                    }
                })
                .catch(err => console.log(err));
        }
    };

    return (
        <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
            <div className='bg-white p-3 rounded w-25'>
                <h2>Create Account</h2>
                <form onSubmit={handleSubmit}>
                    <div className='mb-3'>
                        <label htmlFor='name'><strong>Name</strong></label>
                        <input type='text' placeholder='Enter Name' name='name'
                            onChange={handleInput} className='form-control rounded-0' />
                        {errors.name && <span className='text-danger'> {errors.name}</span>}
                    </div>
                    <div className='mb-3'>
                        <label htmlFor='email'><strong>Email</strong></label>
                        <input type='email' placeholder='Enter Email' name='email'
                            onChange={handleInput} className='form-control rounded-0' />
                        {errors.email && <span className='text-danger'> {errors.email}</span>}
                    </div>
                    <div className='mb-3'>
                        <label htmlFor='password'><strong>Password</strong></label>
                        <input type='password' placeholder='Enter Password' name='password'
                            onChange={handleInput} className='form-control rounded-0' />
                        {errors.password && <span className='text-danger'> {errors.password}</span>}
                    </div>
                    <div className='mb-3'>
                        <input type='checkbox' id='agree' name='agree' onChange={handleCheckbox} />
                        <label htmlFor='agree' className='ms-2'>I agree to terms and conditions</label>
                        {errorMessage && <span className='text-danger'> {errorMessage}</span>}
                    </div>
                    <button type='submit' className='btn btn-success w-100 rounded-0'>Sign up</button>
                </form>
            </div>
        </div>
    );
}

export default Signup;
