import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash } from '@fortawesome/free-solid-svg-icons';
import './CreatePost.css';

function CreatePost() {
    const navigate = useNavigate();
    const [selectedImages, setSelectedImages] = useState([]);
    const [description, setDescription] = useState('');
    const [title, setTitle] = useState('');
    const [price, setPrice] = useState('');
    const [showDiscardModal, setShowDiscardModal] = useState(false);

    const handleImageChange = (e) => {
        if (e.target.files) {
            setSelectedImages([...selectedImages, ...Array.from(e.target.files)]);
        }
    };

    const handleRemoveImage = (index) => {
        const newImages = selectedImages.filter((_, i) => i !== index);
        setSelectedImages(newImages);
    };

    const handleSharePost = () => {
        const formData = new FormData();
        selectedImages.forEach(image => {
            formData.append('images', image);
        });
        formData.append('title', title);
        formData.append('description', description);
        formData.append('price', price);
        formData.append('email', localStorage.getItem('userEmail'));

        axios.post('http://localhost:8081/create-post', formData)
            .then(res => {
                if (res.data.message === "Success") {
                    navigate('/profile');
                } else {
                    console.log("Failed to create post");
                }
            })
            .catch(err => {
                console.log("Error occurred while creating post", err);
            });
    };

    const handleCancelPost = () => {
        setShowDiscardModal(true);
    };

    const handleDiscardPost = () => {
        setShowDiscardModal(false);
        navigate('/profile');
    };

    return (
        <div className="create-post-container">
            <h2>Publish a Post</h2>
            <input
                type="text"
                placeholder="Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                className="form-control mt-2"
            />
            <textarea
                placeholder="Write a caption..."
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                className="form-control mt-2"
            ></textarea>
            <input
                type="text"
                placeholder="Price (€)"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
                className="form-control mt-2 price-input"
            />
            <div className="image-upload-section">
                <label htmlFor="file-input" className="image-upload-label">
                    Add or Drag & Drop up to {15 - selectedImages.length} images
                </label>
                <input
                    id="file-input"
                    type="file"
                    multiple
                    onChange={handleImageChange}
                    className="form-control mt-2"
                    style={{ display: 'none' }}
                />
                <div
                    className="drop-zone"
                    onDragOver={(e) => e.preventDefault()}
                    onDrop={(e) => {
                        e.preventDefault();
                        setSelectedImages([...selectedImages, ...Array.from(e.dataTransfer.files)]);
                    }}
                >
                    <div className="image-preview-container">
                        {selectedImages.map((image, index) => (
                            <div className="image-preview" key={index}>
                                <img
                                    src={URL.createObjectURL(image)}
                                    alt="Selected"
                                    className="selected-image"
                                />
                                <button
                                    className="remove-image-button"
                                    onClick={() => handleRemoveImage(index)}
                                >
                                    <FontAwesomeIcon icon={faTrash} />
                                </button>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
            <button className="btn btn-primary mt-2" onClick={handleSharePost}>Share</button>
            <button className="btn btn-secondary mt-2" onClick={handleCancelPost}>Cancel</button>
            {showDiscardModal && (
                <div className="modal">
                    <div className="modal-content">
                        <p>Discard post?</p>
                        <p>If you leave, your edits won't be saved.</p>
                        <button className="btn btn-danger mt-2" onClick={handleDiscardPost}>Discard</button>
                        <button className="btn btn-secondary mt-2" onClick={() => setShowDiscardModal(false)}>Cancel</button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default CreatePost;
