import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faHome } from '@fortawesome/free-solid-svg-icons';
import './Profile.css';

function Profile() {
    const [user, setUser] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const userData = localStorage.getItem('user');
        if (userData) {
            const parsedData = JSON.parse(userData);
            setUser(parsedData);
        } else {
            navigate('/'); // Redirect to login if user data is not found
        }
    }, [navigate]);

    const handleLogout = () => {
        localStorage.removeItem('user');
        navigate('/');
    };

    const handleNavigateToSettings = () => {
        navigate('/settings');
    };

    const handleNavigateToHome = () => {
        navigate('/home');
    };

    const handlePublishPost = () => {
        // Implement the functionality to publish a post here
    };

    if (!user) {
        return <div>Loading...</div>;
    }

    return (
        <div className="profile-container">
            <div className="profile-header d-flex justify-content-between align-items-center mb-4">
                <h2>
                    <FontAwesomeIcon icon={faUser} /> Profile
                </h2>
                <button className="btn btn-primary" onClick={handleNavigateToHome}>
                    <FontAwesomeIcon icon={faHome} /> Home
                </button>
            </div>
            <div className="profile-card card">
                <div className="card-body">
                    <h3 className="card-title">{user.name}</h3>
                    <p><strong>Email:</strong> {user.email}</p>
                    <button className="btn btn-secondary me-2" onClick={handleNavigateToSettings}>
                        Edit Profile
                    </button>
                    <button className="btn btn-primary me-2" onClick={handlePublishPost}>
                        Publish Post
                    </button>
                    <button className="btn btn-danger" onClick={handleLogout}>
                        Log Out
                    </button>
                </div>
            </div>
        </div>
    );
}

export default Profile;
