import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FilterModal.css';

function FilterModal({ onClose, onFilterChange }) {
  const [genres, setGenres] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState('');
  const [selectedSort, setSelectedSort] = useState('newest');

  useEffect(() => {
    axios.get('http://localhost:8081/genres')
      .then(res => {
        setGenres(res.data);
      })
      .catch(err => {
        console.log("Error fetching genres:", err);
      });
  }, []);

  const applyFilters = () => {
    onFilterChange({ genre: selectedGenre, sortBy: selectedSort });
    onClose();
  };

  return (
    <div className="filter-modal">
      <div className="filter-content">
        <h2>Filter</h2>
        <div className="filter-group">
          <label htmlFor="genre">Genre</label>
          <select 
            id="genre" 
            value={selectedGenre} 
            onChange={(e) => setSelectedGenre(e.target.value)}
          >
            <option value="">All</option>
            {genres.map(genre => (
              <option key={genre.id} value={genre.name}>{genre.name}</option>
            ))}
          </select>
        </div>
        <div className="filter-group">
          <label htmlFor="sort">Sort by</label>
          <select 
            id="sort" 
            value={selectedSort} 
            onChange={(e) => setSelectedSort(e.target.value)}
          >
            <option value="newest">Newest</option>
            <option value="oldest">Oldest</option>
            <option value="priceAsc">Price: Low to High</option>
            <option value="priceDesc">Price: High to Low</option>
          </select>
        </div>
        <button onClick={applyFilters} className="apply-btn">Apply Filters</button>
        <button onClick={onClose} className="close-btn">Close</button>
      </div>
    </div>
  );
}

export default FilterModal;
