import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Home.css';
import Post from './Post';
import Layout from './Layout';
import FilterModal from './FilterModal';

function Home() {
    const [posts, setPosts] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [filter, setFilter] = useState({});
    const [showFilter, setShowFilter] = useState(false);

    useEffect(() => {
        fetchPosts();
    }, [filter]);

    const fetchPosts = async () => {
        try {
            const response = await axios.get('http://localhost:8081/posts', { params: filter });
            setPosts(response.data);
        } catch (error) {
            console.error("Error fetching posts:", error);
        }
    };

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    const filteredPosts = posts.filter(post => post.title.toLowerCase().includes(searchTerm.toLowerCase()));

    return (
        <div className="home-container">
            <div className="search-filter-bar">
                <input
                    type="text"
                    placeholder="Search..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="search-bar"
                />
                <button className="filter-button" onClick={() => setShowFilter(true)}>
                    <i className="fas fa-filter"></i>
                </button>
            </div>

            <div className="posts-container">
                {filteredPosts.length > 0 ? (
                    filteredPosts.map(post => (
                        <Post key={post.id} post={post} />
                    ))
                ) : (
                    <p className="no-posts-message">No posts available.</p>
                )}
            </div>

            {showFilter && <FilterModal onClose={() => setShowFilter(false)} onApplyFilter={setFilter} />}
        </div>
    );
}

export default Home;
