import React, { useState, useEffect } from 'react';
import axios from 'axios';
import FilterModal from './FilterModal';
import './Home.css';
import Layout from './Layout';

const Home = () => {
    const [posts, setPosts] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [showFilterModal, setShowFilterModal] = useState(false);
    const [filters, setFilters] = useState({ sortBy: '', genre: '' });

    useEffect(() => {
        fetchPosts();
    }, [filters]);

    const fetchPosts = () => {
        const queryParams = [];
        if (searchTerm) queryParams.push(`searchTerm=${searchTerm}`);
        if (filters.sortBy) queryParams.push(`sort=${filters.sortBy}`);
        if (filters.genre) queryParams.push(`genre=${filters.genre}`);

        axios.get(`http://localhost:8081/posts?${queryParams.join('&')}`)
            .then(res => {
                setPosts(res.data);
            })
            .catch(err => {
                console.error('Error fetching posts:', err);
            });
    };

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
        fetchPosts();
    };

    const handleApplyFilters = (newFilters) => {
        setFilters(newFilters);
    };

    return (
        <Layout onSearch={handleSearchChange}>
            <div className="home-container">
                <input
                    type="text"
                    className="search-bar"
                    placeholder="Search..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                />
                <button className="filter-button" onClick={() => setShowFilterModal(true)}>
                    <i className="fas fa-filter"></i>
                </button>
                <FilterModal
                    show={showFilterModal}
                    onClose={() => setShowFilterModal(false)}
                    onApplyFilters={handleApplyFilters}
                />
                <div className="posts-grid">
                    {posts.length > 0 ? (
                        posts.map(post => (
                            <div key={post.id} className="post-card">
                                <img src={post.picture_url} alt={post.title} className="post-image" />
                                <div className="post-content">
                                    <h3>{post.title}</h3>
                                    <p>Author: {post.author}</p>
                                    <p>Posted by: {post.user_name}</p>
                                    <p>Price: â‚¬{post.price.toFixed(2)}</p>
                                    <p>Genre: {post.genre}</p>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p>No posts found</p>
                    )}
                </div>
            </div>
        </Layout>
    );
};

export default Home;
