import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Inbox.css';
import { useNavigate } from 'react-router-dom';

function Inbox() {
    const [chats, setChats] = useState([]);
    const navigate = useNavigate();
    const user = JSON.parse(localStorage.getItem('user'));

    useEffect(() => {
        fetchChats();
    }, []);

    const fetchChats = () => {
        axios.get(`http://localhost:8081/inbox/${user.email}`)
            .then(res => {
                console.log('Fetched chats:', res.data); // Debug log
                setChats(res.data);
            })
            .catch(err => {
                console.log('Error fetching chats:', err);
            });
    };

    const handleChatClick = (chatId) => {
        navigate(`/chat/${chatId}`);
    };

    return (
        <div className="inbox-container">
            <h2>Inbox</h2>
            <ul className="chat-list">
                {chats.map((chat, index) => (
                    <li key={index} className="chat-item" onClick={() => handleChatClick(chat.id)}>
                        <img src={`http://localhost:8081/${chat.profile_pic}`} alt={chat.user_name} className="profile-pic" />
                        <span>{chat.user_name}</span>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Inbox;
