import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faSearch } from '@fortawesome/free-solid-svg-icons';
import './Settings.css';

function Settings() {
    const [user, setUser] = useState(null);
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const userData = localStorage.getItem('user');
        if (userData) {
            const parsedData = JSON.parse(userData);
            setUser(parsedData);
            setName(parsedData.name);
            setEmail(parsedData.email);
            setPassword(parsedData.password);
        } else {
            navigate('/login');
        }
    }, [navigate]);

    const handleSave = () => {
        const updatedUser = { ...user, name, email, password };
        axios.post('http://localhost:8081/update-user', updatedUser)
            .then(res => {
                if (res.data.message === "Success") {
                    localStorage.setItem('user', JSON.stringify(updatedUser));
                    setUser(updatedUser);
                    alert('Profile updated successfully');
                } else {
                    console.log("Failed to update profile");
                }
            })
            .catch(err => {
                console.log("Error occurred while updating profile", err);
            });
    };

    const handleLogout = () => {
        localStorage.removeItem('user');
        navigate('/login');
    };

    if (!user) {
        return <div>Loading...</div>;
    }

    return (
        <div className="home-container">
            <div className="header">
                <div className="logo"> 
                    <img src="/logo.png" alt="Logo" />
                </div>
                <form className="search-form">
                    <input type="text" placeholder="Search..." className="search-input" />
                    <button type="submit" className="search-button">
                        <FontAwesomeIcon icon={faSearch} />
                    </button>
                </form>
                <div className="nav-menu">
                    <FontAwesomeIcon icon={faUser} className="nav-icon" />
                    <div className="nav-dropdown">
                        <button onClick={() => navigate('/profile')}>Profile</button>
                        <button onClick={() => navigate('/home')}>Home</button>
                        <button onClick={handleLogout}>Log Out</button>
                    </div>
                </div>
            </div>
            <div className="settings-container">
                <h2>Settings</h2>
                <div className="settings-form">
                    <label>Name:</label>
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <label>Password:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button className="btn btn-primary mt-3" onClick={handleSave}>Save</button>
                </div>
            </div>
        </div>
    );
}

export default Settings;
