import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Inbox.css';

function Inbox({ user }) {
    const [chats, setChats] = useState([]);
    const [selectedChat, setSelectedChat] = useState(null);
    const [message, setMessage] = useState('');

    useEffect(() => {
        const fetchChats = async () => {
            try {
                const res = await axios.get(`http://localhost:8081/chats/${user.id}`);
                setChats(res.data);
            } catch (error) {
                console.error('Error fetching chats:', error);
            }
        };

        fetchChats();
    }, [user.id]);

    const handleSendMessage = async () => {
        if (!message.trim() || !selectedChat) {
            return;
        }

        try {
            const messageData = {
                chat_id: selectedChat.id,
                buyer_id: user.id,
                seller_id: selectedChat.seller_id,
                post_id: selectedChat.post_id,
                postImage: selectedChat.postImage,
                postTitle: selectedChat.postTitle,
                message: message,
                user_email: user.email
            };

            await axios.post('http://localhost:8081/messages', messageData);

            setSelectedChat((prev) => ({
                ...prev,
                messages: [...prev.messages, { ...messageData, timestamp: new Date().toISOString() }]
            }));
            setMessage('');
        } catch (error) {
            console.error('Error sending message:', error);
        }
    };

    const handleChatSelect = async (chat) => {
        try {
            const res = await axios.get(`http://localhost:8081/messages/${chat.id}`);
            setSelectedChat({ ...chat, messages: res.data });
        } catch (error) {
            console.error('Error fetching messages:', error);
        }
    };

    return (
        <div className="inbox-container">
            <div className="inbox-sidebar">
                {chats.map((chat) => (
                    <div
                        key={chat.id}
                        className={`chat-preview ${selectedChat && selectedChat.id === chat.id ? 'selected' : ''}`}
                        onClick={() => handleChatSelect(chat)}
                    >
                        <img src={`http://localhost:8081/${chat.postImage}`} alt={chat.postTitle} className="post-image-small" />
                        <div className="chat-info">
                            <p>{chat.postTitle}</p>
                        </div>
                    </div>
                ))}
            </div>
            <div className="inbox-chat">
                {selectedChat ? (
                    <>
                        <div className="chat-header">
                            <img src={`http://localhost:8081/${selectedChat.postImage}`} alt={selectedChat.postTitle} className="post-image-small" />
                            <h2>{selectedChat.postTitle}</h2>
                        </div>
                        <div className="chat-messages">
                            {selectedChat.messages.map((msg, index) => (
                                <div key={index} className="chat-message">
                                    <div className="message-info">
                                        <span>{msg.user_email}</span>
                                        <span>{new Date(msg.timestamp).toLocaleString()}</span>
                                    </div>
                                    <p>{msg.message}</p>
                                </div>
                            ))}
                        </div>
                        <div className="chat-input">
                            <textarea
                                value={message}
                                onChange={(e) => setMessage(e.target.value)}
                                placeholder="Type your message..."
                            />
                            <button onClick={handleSendMessage}>Send</button>
                        </div>
                    </>
                ) : (
                    <div className="no-chat-selected">Select a chat to view messages</div>
                )}
            </div>
        </div>
    );
}

export default Inbox;
