import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useLocation } from 'react-router-dom';
import './Home.css';
import Layout from './Layout';

function Home() {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const [posts, setPosts] = useState([]);
  const [searchQuery, setSearchQuery] = useState(queryParams.get('searchTerm') || '');
  const [filter, setFilter] = useState({ sortBy: queryParams.get('sortBy') || 'newest', genre: queryParams.get('genre') || '' });

  useEffect(() => {
    fetchPosts();
  }, [filter, searchQuery]);

  const fetchPosts = () => {
    axios.get('http://localhost:8081/posts', { params: { ...filter, searchTerm: searchQuery } })
      .then(res => {
        setPosts(res.data);
      })
      .catch(err => {
        console.log("Error fetching posts:", err);
      });
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
    setFilter({ ...filter, searchTerm: query });
  };

  const handleFilterChange = (newFilter) => {
    setFilter(newFilter);
  };

  return (
    <Layout onSearch={handleSearch} onFilterChange={handleFilterChange}>
      <div className="home-container">
        <div className="posts-list">
          {posts.length ? posts.map(post => (
            <Link key={post.id} to={`/post/${post.id}`} className="post-card">
              <div className="post-price-container">
                <p className="post-price">â‚¬{parseFloat(post.price).toFixed(2)}</p>
              </div>
              <img src={`http://localhost:8081/${post.picture}`} alt={post.title} className="post-image" />
              <div className="post-details">
                <h3 className="post-title">{post.title}</h3>
                <p className="post-author">Author: {post.author}</p>
                <p className="post-genre">Genre: {post.genre}</p>
                <p className="post-user">Posted by: {post.user_name}</p>
              </div>
            </Link>
          )) : <p>No posts found</p>}
        </div>
      </div>
    </Layout>
  );
}

export default Home;
