import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './Home.css';
import Layout from './Layout';
import FilterModal from './FilterModal';

function Home() {
  const [posts, setPosts] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filter, setFilter] = useState({ sortBy: '', genre: '' });
  const [showFilterModal, setShowFilterModal] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchPosts();
  }, [filter, searchQuery]);

  const fetchPosts = () => {
    axios.get('http://localhost:8081/posts', { params: { ...filter, query: searchQuery } })
      .then(res => {
        setPosts(res.data);
      })
      .catch(err => {
        console.log("Error fetching posts:", err);
      });
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const handleFilterChange = (newFilter) => {
    setFilter(newFilter);
  };

  const handleCardClick = (postId) => {
    navigate(`/post/${postId}`);
  };

  return (
    <Layout onSearch={handleSearch} onFilterChange={() => setShowFilterModal(true)}>
      <div className="home-container">
        <div className="posts-list">
          {posts.length ? posts.map(post => (
            <div key={post.id} className="post-card" onClick={() => handleCardClick(post.id)}>
              <img src={`http://localhost:8081/${post.picture}`} alt={post.title} className="post-image" />
              <div className="post-details">
                <p className="post-title">{post.title}</p>
                <p className="post-author">Author: {post.author}</p>
                <p className="post-genre">Genre: {post.genre}</p>
                <p className="post-price">Price: â‚¬{parseFloat(post.price).toFixed(2)}</p>
                <p className="post-user">Posted by: {post.user_name}</p>
              </div>
            </div>
          )) : <p>No posts found</p>}
        </div>
      </div>
      {showFilterModal && <FilterModal onClose={() => setShowFilterModal(false)} onFilterChange={handleFilterChange} />}
    </Layout>
  );
}

export default Home;
