import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Home.css';
import Post from './Post';
import FilterModal from './FilterModal';
import Layout from './Layout';
import { useNavigate } from 'react-router-dom';

function Home() {
    const [posts, setPosts] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [filter, setFilter] = useState({ sortBy: '', genre: '' });
    const [showFilterModal, setShowFilterModal] = useState(false);
    const navigate = useNavigate();
    const user = JSON.parse(localStorage.getItem('user'));

    useEffect(() => {
        fetchPosts();
    }, [searchQuery, filter]);

    const fetchPosts = async () => {
        try {
            const response = await axios.get('http://localhost:8081/posts', {
                params: {
                    search: searchQuery,
                    sortBy: filter.sortBy,
                    genre: filter.genre,
                },
            });
            setPosts(response.data);
        } catch (error) {
            console.error('Error fetching posts:', error);
        }
    };

    const handleSearch = (query) => {
        setSearchQuery(query);
    };

    const handleFilterChange = (newFilter) => {
        setFilter(newFilter);
    };

    const openFilterModal = () => {
        setShowFilterModal(true);
    };

    const closeFilterModal = () => {
        setShowFilterModal(false);
    };

    const handlePostClick = (post) => {
        if (user && post.user_email === user.email) {
            navigate(`/own-post-detail/${post.id}`);
        } else {
            navigate(`/post-detail/${post.id}`);
        }
    };

    return (
        <Layout onSearch={handleSearch}>
            <div className="home-container">
                <div className="filter-icon" onClick={openFilterModal}>
                    <FontAwesomeIcon icon={faFilter} />
                </div>
                <div className="posts-container">
                    {posts.length > 0 ? (
                        posts.map((post) => (
                            <Post key={post.id} post={post} onClick={() => handlePostClick(post)} />
                        ))
                    ) : (
                        <div>No posts available</div>
                    )}
                </div>
                {showFilterModal && (
                    <FilterModal
                        filter={filter}
                        onFilterChange={handleFilterChange}
                        onClose={closeFilterModal}
                    />
                )}
            </div>
        </Layout>
    );
}

export default Home;
