import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Home.css';
import Layout from './Layout';

function Home() {
  const [posts, setPosts] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filter, setFilter] = useState({ sortBy: '', genre: '' });

  useEffect(() => {
    fetchPosts();
  }, [filter]);

  const fetchPosts = () => {
    axios.get('http://localhost:8081/posts')
      .then(res => {
        const postsWithUsernames = res.data.map(post => ({
          ...post,
          user_name: 'Unknown', // Default value
        }));

        const userFetches = postsWithUsernames.map(post =>
          axios.get(`http://localhost:8081/user/${post.user_email}`)
            .then(res => {
              post.user_name = res.data.name;
            })
            .catch(err => {
              console.error(`Error fetching user data for ${post.user_email}:`, err);
            })
        );

        Promise.all(userFetches).then(() => {
          setPosts(postsWithUsernames);
        });
      })
      .catch(err => {
        console.log("Error fetching posts:", err);
      });
  };

  const handleSearch = (e) => {
    e.preventDefault();
    setFilter({ ...filter, query: searchQuery });
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilter({ ...filter, [name]: value });
  };

  return (
    <Layout onSearch={handleSearch}>
      <div className="home-container">
        <form onSubmit={handleSearch} className="search-form">
          <input
            type="text"
            placeholder="Search..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="search-input"
          />
          <button type="submit" className="search-button">
          </button>
        </form>
        <div className="filter-section">
          <select
            name="sortBy"
            value={filter.sortBy}
            onChange={handleFilterChange}
            className="filter-dropdown"
          >
            <option value="">Select</option>
            <option value="newest">Newest First</option>
            <option value="oldest">Oldest First</option>
            <option value="price-asc">Price Ascending</option>
            <option value="price-desc">Price Descending</option>
          </select>
          <select
            name="genre"
            value={filter.genre}
            onChange={handleFilterChange}
            className="filter-dropdown"
          >
            <option value="">All Genres</option>
            <option value="Fiction">Fiction</option>
            <option value="Non-Fiction">Non-Fiction</option>
            <option value="Science Fiction">Science Fiction</option>
            <option value="Biography">Biography</option>
            <option value="Poetry">Poetry</option>
            <option value="Horror">Horror</option>
            <option value="Thriller">Thriller</option>
            <option value="Historical Fiction">Historical Fiction</option>
            <option value="Adventure">Adventure</option>
            <option value="Drama">Drama</option>
            <option value="Self-help">Self-help</option>
            <option value="Travel">Travel</option>
            <option value="Children's">Children's</option>
          </select>
        </div>
        <div className="posts-list">
          {posts.map(post => (
            <div key={post.id} className="post-card">
              <Link to={`/post/${post.id}`}>
                <img src={`http://localhost:8081/${post.picture}`} alt={post.title} className="post-image" />
              </Link>
              <div className="post-details">
                <Link to={`/post/${post.id}`} className="post-title">{post.title}</Link>
                <p className="post-author">Author: {post.author}</p>
                <p className="post-user">Posted by: {post.user_name}</p>
                <p className="post-price">Price: €{parseFloat(post.price).toFixed(2)}</p>
                <p className="post-genre">Genre: {post.genre}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </Layout>
  );
}

export default Home;
