import React from 'react';
import { Link } from 'react-router-dom';

const Post = ({ post }) => {
    const { id, title, author, user, price, genre, image, description } = post;
    return (
        <div className="post">
            <Link to={`/post/${id}`}>
                <img src={image} alt={title} className="post-image" />
            </Link>
            <div className="post-details">
                <Link to={`/post/${id}`}>
                    <h3>{title}</h3>
                </Link>
                <p><strong>Author:</strong> {author}</p>
                <p><strong>Posted by:</strong> {user ? user.name : 'Unknown'}</p>
                <p><strong>Price:</strong> â‚¬{price}</p>
                <p><strong>Genre:</strong> {genre}</p>
                <p>{description}</p>
            </div>
        </div>
    );
};

export default Post;
