import React, { useState } from 'react';
import axios from 'axios';
import './ChatModal.css';

const ChatModal = ({ post, user, onClose }) => {
    const [message, setMessage] = useState('');

    const handleSendMessage = () => {
        const messageData = {
            buyer_id: user.id,
            seller_id: post.user_id,
            post_id: post.id,
            postImage: post.picture, // Ensure the post image and title are sent
            postTitle: post.title,
            message: message
        };

        axios.post('http://localhost:8081/createChat', messageData)
            .then((res) => {
                console.log('Message sent:', res.data);
                onClose();
            })
            .catch((err) => {
                console.error('Error sending message:', err);
            });
    };

    return (
        <div className="chat-modal">
            <div className="chat-modal-header">
                <button onClick={onClose} className="close-button">X</button>
                <h2>Contact Seller</h2>
            </div>
            <div className="chat-modal-body">
                <div className="post-preview">
                    <img src={`http://localhost:8081/${post.picture}`} alt={post.title} className="post-image-preview" />
                    <div>
                        <h3>{post.title}</h3>
                        <p>{post.description}</p>
                    </div>
                </div>
                <textarea
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Type your message..."
                />
            </div>
            <div className="chat-modal-footer">
                <button onClick={handleSendMessage} className="send-button">Send</button>
            </div>
        </div>
    );
};

export default ChatModal;
